import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import "./SinglePost.css";

function SinglePost() {
  const [book, setBook] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/posts/${id}/`)  // Fetch the post by its id
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Fetched Post Detail:", data);
        setBook(data);
      })
      .catch((error) => console.error("Error fetching post detail:", error));
  }, [id]);

  if (!book) {
    return <div>Loading...</div>;  // Display a loading message while the data is being fetched
  }

  return (
    <div className="postDetail">
      <h2>{book.title}</h2>
      <p>Posted By: {book.created_by.name}</p>
      <img src={book.image} alt={book.title} />
      <div className="postContent"
            dangerouslySetInnerHTML={{ __html: book.content }}
          />
    </div>
  );
}

export default SinglePost;
