import "./UpdatePost.css";
import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import ReactMarkdown from 'react-markdown';

function UpdatePost() {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [isLoggedin, setIsLoggedin] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem("auth_token");
    
if (token) {
    setIsLoggedin(true);
    navigate(`/update-profile/${id}`);
} else {
    setIsLoggedin(false);
}

    // Fetch post details
    fetch(`http://127.0.0.1:8000/posts/${id}/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTitle(data.title);
        setContent(data.content);
        setFile(data.image);
      })
      .catch((error) => console.error("Error fetching post detail:", error));
  }, [id]);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    if (file instanceof File) {
      formData.append("image", file); // Only append if a new file is selected
    }

    fetch(`http://127.0.0.1:8000/posts/${id}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${sessionStorage.getItem("auth_token")}`, // Assuming you're using Bearer token
      },
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(() => {
        alert("Post updated successfully!");
        navigate(`/post/${id}`);
      })
      .catch((error) => {
        console.error("Error updating post:", error);
        alert("Failed to update the post.");
      });
  };

  return (
    <div className="write">
      <img
        className="writeImg"
        src={
          file
            ? typeof file === "string"
              ? file // Existing image URL
              : URL.createObjectURL(file) // Preview of selected image
            : "https://quotefancy.com/media/wallpaper/3840x2160/1869852-Dolly-Parton-Quote-Don-t-judge-me-by-the-cover-cause-I-m-a-real.jpg"
        }
        alt="Post Cover"
      />
      {isLoggedin ? (
        <form className="writeForm" onSubmit={handleSubmit}>
          <div className="writeFormGroup">
            <label htmlFor="fileInput">
              <i className="writeIcon fas fa-plus"></i>
            </label>
            <input
              id="fileInput"
              type="file"
              style={{ display: "none" }}
              onChange={(e) => setFile(e.target.files[0])}
            />
            <input
              className="writeInput"
              placeholder="Title"
              type="text"
              autoFocus
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="writeFormGroup">
            <textarea
              className="writeInput writeText"
              placeholder="Write your review..."
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </div>
          <button className="writeSubmit" type="submit">
            Publish
          </button>
        </form>
      ) : (
        <p className="writeLoginMessage">Log in to edit a post</p>
      )}
      {/* Markdown Preview Section */}
      {content && (
        <div className="markdownPreview">
          <h3>Markdown Preview:</h3>
          <ReactMarkdown>{content}</ReactMarkdown>
        </div>
      )}
    </div>
  );
}

export default UpdatePost;
