import { useState, useEffect } from "react";
import "./Posts.css";

export default function Posts() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/books/") // Replace with your API URL
      .then((response) => response.json())
      .then((data) => setBooks(data))
      .catch((error) => console.error("Error fetching books:", error));
  }, []);

  return (
    <div className="posts">
      {books.map((book) => (
        <div className="post" key={book.id}>
          <img className="postImg" src={book.image_url} alt={book.title} />
          <div className="postInfo">
            <span className="postTitle">{book.title}</span>
            <hr />
            <span className="postAuthor">{book.author}</span>
          </div>
          <p className="postDesc">{book.description}</p>
        </div>
      ))}
    </div>
  );
}
