import "./Create.css"
import { useState, useEffect } from "react";

function Create() {
  
  const [isLoggedin, setIsLoggedin] = useState(false);
  const navigate = useNavigate();

  useEffect(()=>{
    const token = sessionStorage.getItem("auth_token");
    if(token){
      setIsLoggedin(true);
    }
    else{
      setIsLoggedin(false);
    }
  },[]);

  return (
    <div className="write">
      <img className="writeImg" src="https://quotefancy.com/media/wallpaper/3840x2160/1869852-Dolly-Parton-Quote-Don-t-judge-me-by-the-cover-cause-I-m-a-real.jpg" alt="" />
      {isLoggedin ? (
        <>
          <form className="writeForm">
            <div className="writeFormGroup">
              <label htmlFor="fileInput">
                <i className="writeIcon fas fa-plus"></i>
              </label>
              <input id="fileInput" type="file" style={{ display: "none" }} />
              <input className="writeInput" placeholder="Title" type="text"autoFocus={true} />
            </div>
            <div className="writeFormGroup">
              <textarea className="writeInput writeText" placeholder="Write your review..." type="text" autoFocus={true} />
            </div>
            <button className="writeSubmit" type="submit">Publish </button>
          </form>
        </>
      ) : (
        <>
        <p> Log in to create a post</p>
        </>
      )}
      
      
      
    </div>
  );
}

export default Create
