import "./Topbar.css";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function TopBar() {
  const [isLoggedin, setIsLoggedin] = useState(false);
  const [id, setID] = useState(null); // Store the user ID
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem("auth_token");
    const userId = sessionStorage.getItem("user_id");

    if (token && userId) {
      setIsLoggedin(true);
      setID(userId); // Set the logged-in user's ID
    } else {
      setIsLoggedin(false);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem("auth_token");
    sessionStorage.removeItem("user_id"); // Clear the user ID on logout
    setIsLoggedin(false);
    setID(null); // Clear the user ID
    navigate("/"); // Redirect to the homepage
  };

  return (
    <div className="top">
      <div className="topLeft">
        <i className="topIcon fab fa-facebook-square"></i>
        <i className="topIcon fab fa-instagram-square"></i>
        <i className="topIcon fab fa-pinterest-square"></i>
        <i className="topIcon fab fa-twitter-square"></i>
      </div>
      <div className="topCenter">
        <ul className="topList">
          <li className="listItem">
            <Link className="link" to="/">HOME</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/About">ABOUT</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Create">CREATE</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Blog">BLOG</Link>
          </li>
        </ul>
      </div>
      <div className="topRight">
        {isLoggedin ? (
          <>
            <li className="profileItem">
              <Link className="link" to={`/user/${id}`}>My Profile</Link>
            </li>
            <li className="profileItem" onClick={handleLogout}>Logout</li>
          </>
        ) : (
          <>
            <ul className="profile">
              <li className="profileItem"><Link className="link" to="/register">Register</Link></li>
              <li className="profileItem"><Link className="link" to="/login">Login</Link></li>
            </ul>
          </>
        )}
      </div>
    </div>
  );
}

export default TopBar;
