import "./Create.css";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function Create() {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [isLoggedin, setIsLoggedin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem("auth_token");
    if (token) {
      setIsLoggedin(true);
    } else {
      setIsLoggedin(false);
      navigate("/login"); // Redirect to login page if not logged in
    }
  }, [navigate]);

  const handleSubmit = async () => {
    const formData = new FormData();
    if (file) {
      formData.append("file", file);
    }
    formData.append("title", title);
    formData.append("content", content);

    try {
      const response = await fetch("http://127.0.0.1:8000/create/", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Blog created successfully:", data);
        // Optionally redirect after successful creation
        navigate("/blogs");
      } else {
        const errorData = await response.json();
        console.error("Failed to create blog:", errorData);
      }
    } catch (err) {
      console.error("An error occurred:", err);
    }
  };

  return (
    <div className="write">
      <img
        className="writeImg"
        src="https://quotefancy.com/media/wallpaper/3840x2160/1869852-Dolly-Parton-Quote-Don-t-judge-me-by-the-cover-cause-I-m-a-real.jpg"
        alt=""
      />
      {isLoggedin ? (
        <>
          <form
            className="writeForm"
            onSubmit={(e) => {
              e.preventDefault();
              handleSubmit();
            }}
          >
            <div className="writeFormGroup">
              <label htmlFor="fileInput">
                <i className="writeIcon fas fa-plus"></i>
              </label>
              <input
                id="fileInput"
                type="file"
                style={{ display: "none" }}
                onChange={(e) => setFile(e.target.files[0])} // Store the File object
              />
              <input
                className="writeInput"
                placeholder="Title"
                type="text"
                autoFocus={true}
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="writeFormGroup">
              <textarea
                className="writeInput writeText"
                placeholder="Write your review..."
                dangerouslySetInnerHTML={{ __html: book.content }}
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </div>
            <button className="writeSubmit" type="submit">
              Publish
            </button>
          </form>
        </>
      ) : (
        <p className="writeLoginMessage">Log in to create a post</p>
      )}
    </div>
  );
}

export default Create;
