import "./Create.css"
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function Create() {

  const [file,setFile] = useState("");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("") 
  const [isLoggedin, setIsLoggedin] = useState(false);
  const navigate = useNavigate();

  useEffect(()=>{
    const token = sessionStorage.getItem("auth_token");
    if(token){
      setIsLoggedin(true);
      navigate('/Create')
    }
    else{
      setIsLoggedin(false);
    }
  },[navigate])

  const handleSubmit = async () => {
    const blogData = { file, title, content }; // Ensure these variables are defined in your component
    try {
        const response = await fetch("http://127.0.0.1:8000/create/", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(blogData)
        });

    } catch (err) {
        console.error("An error occurred:", err);
    }
};


  return (
    <div className="write">
      <img className="writeImg" src="https://quotefancy.com/media/wallpaper/3840x2160/1869852-Dolly-Parton-Quote-Don-t-judge-me-by-the-cover-cause-I-m-a-real.jpg" alt="" />
      {isLoggedin ? (
        <>
          <form className="writeForm" onSubmit={(e) => {
                    e.preventDefault();
                    handleSubmit();
                }}>
            <div className="writeFormGroup">
              <label htmlFor="fileInput">
                <i className="writeIcon fas fa-plus"></i>
              </label>
              <input id="fileInput" type="file" value={file} style={{ display: "none" }} onChange={(e) => setFile(e.target.value)} />
              <input className="writeInput" placeholder="Title" type="text"autoFocus={true} value = {title} onChange={(e) => setTitle(e.target.value)}/>
            </div>
            <div className="writeFormGroup">
              <textarea className="writeInput writeText" placeholder="Write your review..." type="text" autoFocus={true} value={content} onChange = {(e) => setContent(e.target.value)}/>
            </div>
            <button className="writeSubmit" type="submit">Publish </button>
          </form>
        </>
      ) : (
        <>
          <p className="writeLoginMessage">Log in to create a post</p>
        </>
      )}
    </div>
  );
}

export default Create
