import "./Topbar.css";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function TopBar() {
  const [isLoggedIn, setIsLoggedIn] = useState(!!sessionStorage.getItem("auth_token")); // Check initial state
  const [profilePic, setProfilePic] = useState(sessionStorage.getItem("profile_pic") || "");
  const navigate = useNavigate();

  useEffect(() => {
    const handleStorageChange = () => {
      const token = sessionStorage.getItem("auth_token");
      const profilePic = sessionStorage.getItem("profile_pic");

      setIsLoggedIn(!!token); // Update login status dynamically
      setProfilePic(profilePic || ""); // Update profile picture dynamically
    };

    // Add an event listener to detect changes in sessionStorage
    window.addEventListener("storage", handleStorageChange);

    return () => {
      window.removeEventListener("storage", handleStorageChange); // Cleanup listener
    };
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem("auth_token");
    sessionStorage.removeItem("profile_pic");
    setIsLoggedIn(false);
    setProfilePic("");
    navigate("/"); // Redirect to the homepage
  };

  return (
    <div className="top">
      <div className="topLeft">
        <i className="topIcon fab fa-facebook-square"></i>
        <i className="topIcon fab fa-instagram-square"></i>
        <i className="topIcon fab fa-pinterest-square"></i>
        <i className="topIcon fab fa-twitter-square"></i>
      </div>
      <div className="topCenter">
        <ul className="topList">
          <li className="listItem">
            <Link className="link" to="/">HOME</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/About">ABOUT</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Create">CREATE</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Blog">BLOG</Link>
          </li>
        </ul>
      </div>
      <div className="topRight">
        <ul className="profile">
          {!isLoggedIn ? (
            <>
              <li className="profileItem">Register</li>
              <li className="profileItem">
                <Link className="link" to="/Login">Login</Link>
              </li>
            </>
          ) : (
            <>
              <li className="profileItem">
                {profilePic ? (
                  <img className="profilePic" src={profilePic} alt="Profile" />
                ) : (
                  <i className="fas fa-user-circle"></i>
                )}
              </li>
              <li className="profileItem" onClick={handleLogout}>
                <Link className="link" to="/">Logout</Link>
              </li>
            </>
          )}
        </ul>
        <i className="topSearchIcon fas fa-search"></i>
      </div>
    </div>
  );
}

export default TopBar;
