import "./Topbar.css";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function TopBar() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  // Function to check the login state
  const updateLoginState = () => {
    const token = sessionStorage.getItem("auth_token");
    setIsLoggedIn(!!token);
  };

  // UseEffect to listen to storage changes
  useEffect(() => {
    updateLoginState();

    // Add storage event listener to detect session changes
    window.addEventListener("storage", updateLoginState);

    return () => {
      // Cleanup event listener on component unmount
      window.removeEventListener("storage", updateLoginState);
    };
  }, []);

  const handleLogout = () => {
    // Clear session and update state
    sessionStorage.removeItem("auth_token");

    // Trigger manual storage event to update other components
    window.dispatchEvent(new Event("storage"));

    navigate("/"); // Navigate to home page after logout
  };

  return (
    <div className="top">
      <div className="topLeft">
        <i className="topIcon fab fa-facebook-square"></i>
        <i className="topIcon fab fa-instagram-square"></i>
        <i className="topIcon fab fa-pinterest-square"></i>
        <i className="topIcon fab fa-twitter-square"></i>
      </div>
      <div className="topCenter">
        <ul className="topList">
          <li className="listItem">
            <Link className="link" to="/">HOME</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/About">ABOUT</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Create">CREATE</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Blog">BLOG</Link>
          </li>
        </ul>
      </div>
      <div className="topRight">
        <ul className="profile">
          {!isLoggedIn ? (
            <>
              <li className="profileItem">Register</li>
              <li className="profileItem">
                <Link className="link" to="/Login">Login</Link>
              </li>
            </>
          ) : (
            <>
              <li className="profileItem">
                <i className="fas fa-user-circle"></i>
              </li>
              <li className="profileItem" onClick={handleLogout}>
                Logout
              </li>
            </>
          )}
        </ul>
      </div>
    </div>
  );
}

export default TopBar;
