import "./Topbar.css";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function TopBar() {

  const [isLoggedin, setIsLoggedin] = useState(false);
  const [userId, setUserId] = useState(null); // To store the logged-in user's ID
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem("auth_token");
    if (token) {
      setIsLoggedin(true);
      const userIdFromToken = sessionStorage.getItem("user_id"); // Example of user ID from session storage
      setUserId(userIdFromToken); // Store the user ID in state
      navigate("/"); // You can redirect to a specific page if needed
    } else {
      setIsLoggedin(false);
    }
  }, [navigate]);

  const handleLogout = () => {
    sessionStorage.removeItem("auth_token");
    sessionStorage.removeItem("user_id"); // Clear the user ID as well
    setIsLoggedin(false);
    navigate("/"); // Redirect to homepage or login
  };

  return (
    <div className="top">
      <div className="topLeft">
        {/* Social Media Icons */}
      </div>
      <div className="topCenter">
        <ul className="topList">
          <li className="listItem">
            <Link className="link" to="/">HOME</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/About">ABOUT</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Create">CREATE</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Blog">BLOG</Link>
          </li>
        </ul>
      </div>
      <div className="topRight">
        {isLoggedin ? (
          <>
            <li className="profileItem">
              {/* Pass the userId to the Profile page */}
              <Link className="link" to={`/user/${userId}`}>My Profile</Link>
            </li>
            <li className="profileItem" onClick={handleLogout}>Logout</li>
          </>
        ) : (
          <ul className="profile">
            <li className="profileItem"><Link className="link" to="/register">Register</Link></li>
            <li className="profileItem"><Link className="link" to="/login">Login</Link></li>
          </ul>
        )}
      </div>
    </div>
  );
}

export default TopBar;
