import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function TopBar() {
  const [isLoggedin, setIsLoggedin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem("auth_token");
    if (token) {
      setIsLoggedin(true);
      navigate("/Profile");  // Redirect to Profile if logged in
    } else {
      setIsLoggedin(false);
    }
  }, [navigate]);

  const handleLogout = () => {
    sessionStorage.removeItem("auth_token");
    setIsLoggedin(false);
    navigate("/");  // Redirect to home after logout
  };

  return (
    <div className="top">
      <div className="topLeft">
        {/* Social Media Icons */}
      </div>
      <div className="topCenter">
        <ul className="topList">
          {/* Navigation Links */}
        </ul>
      </div>
      <div className="topRight">
        {isLoggedin ? (
          <>
            <li className="profileItem">
              <Link className="link" to="/Profile">My Profile</Link>
            </li>
            <li className="profileItem" onClick={handleLogout}>Logout</li>
          </>
        ) : (
          <>
            <ul className="profile">
              <li className="profileItem"><Link className="link" to="/register">Register</Link></li>
              <li className="profileItem"><Link className="link" to="/login">Login</Link></li>
            </ul>
          </>
        )}
      </div>
    </div>
  );
}

export default TopBar;
