import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import DOMPurify from "dompurify";
import { marked } from "marked"; 
import "./SinglePost.css";

function SinglePost() {
  const [book, setBook] = useState(null);
  const { id } = useParams();
  const [isLoggedin, setIsLoggedin] = useState(false);
  const navigate = useNavigate();
  const [comments, setComments] = useState([]);

  useEffect(() => {

    const token = sessionStorage.getItem("auth_token");
    if(token){
      setIsLoggedin(true);
      navigate('/post/:id')
    }
    else{
      setIsLoggedin(false);
      
    }

    fetch(`http://127.0.0.1:8000/posts/${id}/`)  // Fetch the post by its id
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Fetched Post Detail:", data);
        setBook(data);
      })
      .catch((error) => console.error("Error fetching post detail:", error));
  }, [id,navigate]);

  if (!book) {
    return <div>Loading...</div>;  // Display a loading message while the data is being fetched
  }
  const sanitizedContent = DOMPurify.sanitize(marked(book.content));
  return (
    <div>
      <div className="postDetail">
        <h2>{book.title}</h2>
        <p className='postedby'>Posted By: {book.created_by.name}</p>
        <img src={book.image} alt={book.title} />
        <div className="postContent" dangerouslySetInnerHTML={{ __html: sanitizedContent }} />
      </div>
      <div className="commentSection">
        <p className="comment">Leave a reply</p>
        <textarea className="commentbox" placeholder="Your comment here..." />
        <div className='commentInfo'>
          <div className='name'>
            <label>Name : </label><br/>
            <input type="text" className='commentInput' placeholder='Your Name' />
          </div>
          <div className='email'>
            <label>Email : </label><br/>
            <input type="text" className='commentInput' placeholder='Your Email' />
          </div>
        </div>
        <button type='submit' className='postButton'>Post Comment</button>
      </div>
    </div>
  );
}

export default SinglePost;
