import { useState, useEffect } from "react";
import "./Posts.css";

export default function Posts() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/posts/")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Fetched Posts:", data);
        setBooks(data);
      })
      .catch((error) => console.error("Error fetching books:", error));
  }, []);

  return (
    <div className="posts">
      {books.map((book) => (
        <div className="post" key={book.id}>
          <img className="postImg" src={book.image_url} alt={bookimage} />
          <div className="postInfo">
            <span className="postTitle">{book.title}</span>
            <hr />
            <span className="postAuthor">{book.author}</span>
          </div>
          <p className="postDesc">{book.description}</p>
        </div>
      ))}
    </div>
  );
}
