import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import "./Blog.css";

function Blog() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/posts/")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Fetched Posts:", data);
        setBooks(data);
      })
      .catch((error) => console.error("Error fetching books:", error));
  }, []);

  return (
    <div className="blog">
      <div className="blogTitle">
        <h2>All Posts</h2>
        <hr />
      </div>    
      <div className="posts">
        {books.map((book) => (
          <div className="post" key={book.id}>
            {/* Link wrapping the image */}
            <Link to={`/post/${book.id}`}>
              <img className="postImg" src={book.image} alt="bookimage" />
            </Link>

            <div className="postInfo">
              {/* Link wrapping the title */}
              <Link to={`/post/${book.id}`} className="postTitle">
                {book.title}
              </Link>
              <hr />
              <span className="postAuthor">Posted By: {book.created_by.name}</span>
            </div>
            <p className="postDesc">{book.content}</p>
            <Link to={`/post/${book.id}`} className="readMoreLink">Read More</Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Blog;
