import { Link } from "react-router-dom";
import "./Post.css";
import SinglePost from "../SinglePost/SinglePost";

export default function Post({img}) {
  return (
    <div className="post">
      <img className="postImg" src={img} alt=""/>
      <div className="postInfo">
        <span className="postTitle">
          <Link to="/SinglePost" className="singlePost">"The Palace of Illusions: Draupadi's Voice Unveiled"</Link>
        </span>
        <hr />
        <span className="postDate">1 hour ago</span>
      </div>
      <p className="postDesc">
        Step into the world of Draupadi, the enigmatic heroine of the Mahabharata, as she narrates her story with a voice filled with strength, vulnerability, and wit. 
        **"The Palace of Illusions"** by Chitra Banerjee Divakaruni is a captivating tale that reimagines an ancient epic through a modern feminist lens.  
        Whether it's her longing for autonomy, her complex relationships, or her role in the great war, this book is an unputdownable journey into history, mythology, and the resilience of women.  
        A must-read for lovers of poignant storytelling!
      </p>
    </div>
  );
}
