import "./Topbar.css";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

function TopBar() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = sessionStorage.getItem("auth_token");
    if (token) {
      setIsLoggedIn(true);  
    } else {
      setIsLoggedIn(false); // User is not logged in
    }
  }, []);

  const handleLogout = () => {
    
    sessionStorage.removeItem("auth_token");
    setIsLoggedIn(false);  // Update the state to reflect logout
  };

  return (
    <div className='top'>
       <div className="topLeft">
        <i className="topIcon fab fa-facebook-square"></i>
        <i className="topIcon fab fa-instagram-square"></i>
        <i className="topIcon fab fa-pinterest-square"></i>
        <i className="topIcon fab fa-twitter-square"></i>
       </div>
       <div className="topCenter">
        <ul className="topList">
          <li className="listItem">
            <Link className="link" to="/">HOME</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/About">ABOUT</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Create">CREATE</Link>
          </li>
          <li className="listItem">
            <Link className="link" to="/Blog">BLOG</Link>
          </li>
        </ul>
       </div>
       <div className="topRight">
        <ul className="profile">
          {!isLoggedIn ? (
            <>
              <li className="profileItem">Register</li>
              <li className="profileItem"><Link className="link" to="/Login">Login</Link></li>
            </>
          ) : (
            <li className="profileItem" onClick={handleLogout}>
              <Link className="link" to="/">Logout</Link>
            </li>
          )}
        </ul>
        <img className="topImg" src="https://images.pexels.com/photos/1858175/pexels-photo-1858175.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500" alt="" />
        <i className="topSearchIcon fas fa-search"></i>
       </div>
    </div>
  );
}

export default TopBar;
